import Parse from "parse";

/* Generates new Parse comment, saves to Back4App database*/
export const createComment = (parentId = null) => {
  const comment = new Parse.Comment();

  comment.set("body", comment.body);
  comment.set("userId", comment.userId);
  comment.set("parentId", parentId);
  comment.set("username", comment.username);

  return comment.save().then((result) => {
    return result;
  });
};

export const getComments = () => {
    return [
      {
        id: "1",
        body: "First comment",
        username: "Jack",
        userId: "1",
        parentId: null,
        createdAt: "2021-08-16T23:00:33.010+02:00",
      },
      {
        id: "2",
        body: "Second comment",
        username: "John",
        userId: "2",
        parentId: null,
        createdAt: "2021-08-16T23:00:33.010+02:00",
      },
      {
        id: "3",
        body: "First comment first child",
        username: "John",
        userId: "2",
        parentId: "1",
        createdAt: "2021-08-16T23:00:33.010+02:00",
      },
      {
        id: "4",
        body: "Second comment second child",
        username: "John",
        userId: "2",
        parentId: "2",
        createdAt: "2021-08-16T23:00:33.010+02:00",
      },
    ];
  };

  export const updateComment = (text) => {
    return { text };
  };

  export const deleteComment = () => {
    return {};
  };
