import React, { useEffect, useState } from "react";
import {
  getFriend
} from "../../Common/Services/FriendService";
import {
    pullFriend
  } from "../../Common/Services/PullFriendService";
  import {
    getFriendId
  } from "../../Common/Services/FriendIdService";
import {
  addFriend
} from "../../Common/Services/AddFriend";
import {FriendsList} from "./FriendsForm";
import Parse from 'parse';


const ProfileView = () => {

  const [name, setName] = useState();
  const [highScore, setScore] = useState();
  const [userName, setUserName] = useState();
  const [friends, setFriends] = useState(false);
  const [friendArray, setFriendArray] = useState([]);
  const [response, setResponse] = useState();

  useEffect(() => {
    /// Turn into a service
    var currentUser = Parse.User.current()
    currentUser.fetch()
    ///
    var username = currentUser.attributes.username;
    var firstName = currentUser.attributes.firstName;
    var lastName = currentUser.attributes.lastName;
    var score = currentUser.attributes.score;
    var fullName = firstName + ' ' + lastName;

    console.log("highScore1")
    console.log(highScore)
    setUserName(username)
    setName(fullName)
    setScore(score)
    console.log("highScore")
    console.log(highScore)

    setFriends(true)
    console.log("friends2")
    console.log(friends)

    getFriend().then((FriendObjects) =>{
        console.log("FriendObjects")
        console.log(FriendObjects[0].attributes.friend)

        //setFriends([...friends, FriendObjects[0].attributes.friend])
        setFriends(true)


        FriendObjects.map((friend) => {
            console.log("friend")
            console.log(friend.attributes.friend)

            console.log("friends")
            console.log(friends)


            setFriends([friend.attributes.friend, ...friends])

            
        })

    });

  }, []);


  const onClickHandler = (e) => {
    e.preventDefault();

    if (response) {
        getFriendId(response).then((Id) => {
            addFriend(Id).then((result) => {
                console.log(result)
                });
        })
    }
  };

  const onChangeHandler = (e) => {
    e.preventDefault();
    console.log(e.target.value);
    setResponse(e.target.value);
  };

  return (
  <div>
      <h1 className="head">Profile</h1>
      <div>
        <p>Name: {name}</p>
        <p>Username: {userName}</p>
        <p>High Score: {highScore}</p>
      </div>
      <br />
      <h2>Friends List: </h2>
      
  </div>
  );
};
  
  export default ProfileView;

